package trie;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.LinkedList;
import java.util.List;

/**
 * Search Suggestions System
 *
 * Given an array of strings products and a string searchWord. We want to design a system that suggests at most three
 * product names from products after each character of searchWord is typed. Suggested products should have common prefix
 * with the searchWord. If there are more than three products with a common prefix return the three lexicographically
 * minimums products.
 * Return list of lists of the suggested products after each character of searchWord is typed.
 *
 * Example 1:
 *
 * Input: products = ["mobile","mouse","moneypot","monitor","mousepad"], searchWord = "mouse"
 * Output: [
 * ["mobile","moneypot","monitor"],
 * ["mobile","moneypot","monitor"],
 * ["mouse","mousepad"],
 * ["mouse","mousepad"],
 * ["mouse","mousepad"]
 * ]
 * Explanation: products sorted lexicographically = ["mobile","moneypot","monitor","mouse","mousepad"]
 * After typing m and mo all products match and we show user ["mobile","moneypot","monitor"]
 * After typing mou, mous and mouse the system suggests ["mouse","mousepad"]
 * Example 2:
 *
 * Input: products = ["havana"], searchWord = "havana"
 * Output: [["havana"],["havana"],["havana"],["havana"],["havana"],["havana"]]
 * Example 3:
 *
 * Input: products = ["bags","baggage","banner","box","cloths"], searchWord = "bags"
 * Output: [["baggage","bags","banner"],["baggage","bags","banner"],["baggage","bags"],["bags"]]
 * Example 4:
 *
 * Input: products = ["havana"], searchWord = "tatiana"
 * Output: [[],[],[],[],[],[],[]]
 *
 * Constraints:
 *
 * 1 <= products.length <= 1000
 * There are no repeated elements in products.
 * 1 <= Î£ products[i].length <= 2 * 10^4
 * All characters of products[i] are lower-case English letters.
 * 1 <= searchWord.length <= 1000
 * All characters of searchWord are lower-case English letters.
 */

public class SearchSuggestionsSystem {
  public List<List<String>> suggestedProducts(String[] products, String searchWord) {
    List<List<String>> suggestions = new ArrayList<>(searchWord.length());
    if (products == null || products.length == 0) {
      return suggestions;
    }
    Trie trie = build(products);
    for (int j = 0; j < searchWord.length(); j++) {
      trie = trie.data[searchWord.charAt(j) - 'a'];
      if (trie == null) {
        // If trie was null at any point, that means no more words would match, hence we add empty list for the remaining length and break
        for (int k = j; k < searchWord.length(); k++) {
          suggestions.add(Collections.EMPTY_LIST);
        }
        break;
      } else {
        suggestions.add(trie.products);
      }
    }
    return suggestions;
  }

  // Build a trie tree
  private Trie build(String[] p) {
    Arrays.sort(p);
    Trie trie = new Trie();
    for (String s : p) {
      Trie t = trie;
      for (char c : s.toCharArray()) {
        int i = c - 'a';
        if (t.data[i] == null) {
          t.data[i] = new Trie();
        }
        t = t.data[i];
        if (t.products.size() < 3) {
          t.products.add(s);
        }
      }
    }
    return trie;
  }

  private static class Trie {
    Trie[] data;
    List<String> products;
    Trie() {
      data = new Trie[26];
      products = new LinkedList<>();
    }
  }

  public static void main(String[] args) {
    String[] products = {"",
        "",
        "",
        ""
    };

    String searchWord = "";

    SearchSuggestionsSystem s = new SearchSuggestionsSystem();
    List<List<String>> ans = s.suggestedProducts(products, searchWord);
    for (List<String> l : ans) {
      System.out.println(l);
    }
  }
}
